function drawPie(e){content=[],e.forEach(function(e){content.push({label:e[0],value:e[1],color:"#"+(16777215*Math.random()<<0).toString(16)})});new d3pie("pieChart",{header:{title:{text:"",fontSize:24,font:"helvetica"},subtitle:{color:"#999999",fontSize:12,font:"open sans"},titleSubtitlePadding:9},footer:{color:"#999999",fontSize:10,font:"open sans",location:"bottom-left"},size:{canvasHeight:700,canvasWidth:700},data:{sortOrder:"value-desc",content:content},labels:{outer:{hideWhenLessThanPercentage:1,pieDistance:32},inner:{hideWhenLessThanPercentage:3},mainLabel:{fontSize:11},percentage:{color:"#ffffff",decimalPlaces:0},value:{color:"#adadad",fontSize:11},lines:{enabled:!0}},effects:{pullOutSegmentOnClick:{effect:"linear",speed:400,size:8}},misc:{gradient:{enabled:!0,percentage:100}},callbacks:{onClickSegment:function(e){$.ajax({url:"/word/"+e.data.label,success:function(t){callModalFromCloud(t,e.data.label)}})}}})}